INSERT INTO praca_grupowa.reviews (`rating`, `review_date`, `review_text`, `id_book`, `id_user`) VALUES
  (5, '2025-06-04 12:00:00', 'Przerażająca wizja totalitarnego państwa, które zmusza czytelnika do refleksji nad ceną wolności i prawdy.', 1, 4),
  (5, '2025-06-04 13:00:00', 'Uderzająco przewidująca wizja społeczeństwa kontrolowanego przez przyjemność i inżynierię społeczną, w której jednostka zostaje zdegradowana do roli trybika.', 2, 5),
  (5, '2025-06-04 14:00:00', 'Pełna uroku i przygód podróż Bilba Bagginsa, która wprowadza w świat Śródziemia z humorem i ciepłem.', 3, 6),
  (5, '2025-06-04 15:00:00', 'Magiczna opowieść o odkrywaniu własnej tożsamości i przyjaźni, która czaruje zarówno młodych, jak i dorosłych czytelników.', 4, 7),
  (5, '2025-06-04 16:00:00', 'Tragiczną historią Jay’a Gatsby’ego i jego niespełnionej miłości Fitzgerald ukazuje pustkę i rozczarowanie świata amerykańskiego snu.', 5, 8),
  (5, '2025-06-04 17:00:00', 'Poruszająca i uniwersalna kronika rasizmu i niewinności widziana oczami chłopca w małym południowym miasteczku.', 6, 9),
  (5, '2025-06-04 18:00:00', 'Błyskotliwa i dowcipna analiza barier społecznych i uprzedzeń w epoce regencji, z niezapomnianą postacią Elizabeth Bennet.', 7, 10),
  (5, '2025-06-04 19:00:00', 'Autentyczny portret buntu nastolatka, który swoją szczerością i ironią do dziś przemawia do młodych czytelników.', 8, 11),
  (5, '2025-06-04 20:00:00', 'Epicka saga pełna odwagi i poświęcenia, w której przyjaźń i nadzieja stają się bronią przeciwko mrokom świata.', 9, 12),
  (5, '2025-06-04 21:00:00', 'Alarmująca wizja przyszłości, w której książki są zakazane, a społeczeństwo utrzymywane w stanie uśpienia dzięki bezrefleksyjnej rozrywce.', 10, 13),
  (5, '2025-06-04 22:00:00', 'Wciągający thriller łączący elementy historii, religii i zagadek, który trzyma w napięciu do ostatniej strony.', 11, 14),
  (5, '2025-06-04 23:00:00', 'Inspirująca opowieść o podróży w głąb siebie, która przypomina, że prawdziwe skarby często kryją się blisko nas.', 12, 15),
  (5, '2025-06-05 00:00:00', 'Mroczny i złożony kryminał, w którym autorka tworzy barwne postacie i skomplikowaną sieć spisków.', 13, 16),
  (5, '2025-06-05 01:00:00', 'Przerażająca historia małżeństwa, w której narracyjne zwroty akcji i manipulacje bohaterów wciągają czytelnika w grę pełną obłudy.', 14, 17),
  (5, '2025-06-05 02:00:00', 'Ostra satyra na społeczeństwo spektaklu, a jednocześnie poruszająca opowieść o odwadze i walce o przetrwanie.', 15, 18),
  (5, '2025-06-05 03:00:00', 'Wzruszająca i prawdziwa historia młodzieńczej miłości, która przypomina o wartości każdego oddechu.', 16, 19),
  (5, '2025-06-05 04:00:00', 'Pełna magii i symboliki opowieść, która w subtelny sposób ukazuje odwieczną walkę dobra ze złem.', 17, 20),
  (5, '2025-06-05 05:00:00', 'Przystępne wprowadzenie w fizykę teoretyczną, które z pasją wyjaśnia tajemnice wszechświata i czasoprzestrzeni.', 18, 4),
  (5, '2025-06-05 06:00:00', 'Przejmująca, niemal poetycka relacja ojca i syna próbujących przetrwać w postapokaliptycznym świecie.', 19, 5),
  (5, '2025-06-05 07:00:00', 'Niesamowicie gęsta atmosfera grozy, w której odizolowane hotelowe mury stają się areną dla demonów ludzkiego umysłu.', 20, 6),
  (5, '2025-06-05 08:00:00', 'Przerażająca wizja teokratycznego państwa, w którym kobieta zostaje sprowadzona do roli inkubatora, a wolność kończy się w chwili utraty tożsamości.', 21, 7),
  (5, '2025-06-05 09:00:00', 'Poruszająca historia przyjaźni, zdrady i odkupienia na tle dramatycznych zmian w historii Afganistanu.', 22, 8),
  (5, '2025-06-05 10:00:00', 'Wzruszająca opowieść o sile słowa i człowieczeństwa widziana oczami młodej dziewczynki w nazistowskich Niemczech.', 23, 9),
  (5, '2025-06-05 11:00:00', 'Fascynujące studium mechanizmów podejmowania decyzji, które otwiera oczy na liczne błędy poznawcze, jakich wszyscy się dopuszczamy.', 24, 10),
  (5, '2025-06-05 12:00:00', 'Pasjonująca kronika rozwoju człowieka, która wprost zachwyca głębią analizy i szeroką perspektywą historyczną.', 25, 11),
  (5, '2025-06-05 13:00:00', 'Silna i prawdziwa autobiografia, która pokazuje, jak edukacja może uwolnić człowieka nawet w najbardziej opresyjnym środowisku.', 26, 12),
  (5, '2025-06-05 14:00:00', 'Intymne spojrzenie na drogę Michelle Obamy od dzieciństwa po Biały Dom, pełne mądrości i inspiracji.', 27, 13),
  (5, '2025-06-05 15:00:00', 'Bezkompromisowy poradnik, który w prosty sposób uczy priorytetyzacji wartości i akceptacji własnych ograniczeń.', 28, 14),
  (5, '2025-06-05 16:00:00', 'Praktyczny przewodnik po wprowadzaniu drobnych, ale skutecznych zmian w codziennych nawykach, który pomaga osiągnąć wielkie cele.', 29, 15),
  (5, '2025-06-05 17:00:00', 'Wciągające, nieprzewidywalne studium obsesji i złudzeń narratorki, która wplątuje się w mroczne wydarzenia obserwowane z okna pociągu.', 30, 16),
  (5, '2025-06-05 18:00:00', 'Monumentalna saga, w której polityka, religia i ekologia splatają się w niepowtarzalną opowieść o władzy i przeznaczeniu.', 31, 17),
  (5, '2025-06-05 19:00:00', 'Dynamiczna i inteligentna historia młodego geniusza szkolonego do ratowania ludzkości, w której pytania o moralność wojny wybrzmiewają szczególnie dobitnie.', 32, 18),
  (5, '2025-06-05 20:00:00', 'Kultowa powieść cyberpunkowa, która zapoczątkowała gatunek, malując mroczną wizję przyszłości pełną korporacyjnych spisków i sztucznej inteligencji.', 33, 19),
  (5, '2025-06-05 21:00:00', 'Pełna absurdalnego humoru i fantastycznych pomysłów podróż przez kosmos, która bawi i zmusza do zastanowienia nad sensem istnienia.', 34, 20),
  (5, '2025-06-05 22:00:00', 'Mrożący krew w żyłach klasyk gotycki, w którym wampir hrabia wprowadza terror wśród mieszkańców wiktoriańskiej Anglii.', 35, 4),
  (5, '2025-06-05 23:00:00', 'Przerażająca i jednocześnie tragiczna opowieść o pragnieniu stworzenia życia, która stawia pytania o granice nauki i odpowiedzialność za swoje czyny.', 36, 5),
  (5, '2025-06-06 00:00:00', 'Epicka powieść o miłości, odkupieniu i niesprawiedliwości społecznej, której piękno tkwi w złożonych portretach postaci i monumentalnej panoramie Francji.', 37, 6),
  (5, '2025-06-06 01:00:00', 'Monumentalna saga historyczna, w której losy bohaterów splatają się z dramatycznymi wydarzeniami wojen napoleońskich, pozostawiając czytelnika z poczuciem ogromu ludzkich dramatów.', 38, 7),
  (5, '2025-06-06 02:00:00', 'Psychologiczne studium winy i kary, w którym Raskolnikow prowadzi czytelnika przez mroki własnej duszy, poszukując odkupienia.', 39, 8),
  (5, '2025-06-06 03:00:00', 'Ponadczasowa epopeja o powrocie do domu, w której odnajdujemy wiarę w wytrwałość i przebiegłość ludzkiego ducha.', 40, 9),
  (5, '2025-06-06 04:00:00', 'Wspaniała opowieść o gniewie Achillesa i heroizmie wojowników trojańskich, która ukazuje surowe oblicze wojny i ludzkiej doli.', 41, 10),
  (5, '2025-06-06 05:00:00', 'Alegoryczna podróż przez zaświaty, w której Dante w mistrzowski sposób łączy teologię, filozofię i literaturę, tworząc arcydzieło światowej kultury.', 42, 11),
  (5, '2025-06-06 06:00:00', 'Hipnotyzująca opowieść kapitana Ahaba o obsesyjnej pogoni za białym wielorybem, która odkrywa przed czytelnikiem głębię ludzkiego szaleństwa.', 43, 12),
  (5, '2025-06-06 07:00:00', 'Poruszająca historia o sile charakteru i niezłomnej woli kobiety, która nie boi się walczyć o miłość i godność w trudnych czasach.', 44, 13),
  (5, '2025-06-06 08:00:00', 'Dzika i namiętna opowieść o miłości i zemście, której brutalny realizm i intensywne emocje pozostają w pamięci na długo.', 45, 14),
  (5, '2025-06-06 09:00:00', 'Mroczna refleksja nad pięknem, moralnością i ceną hedonizmu, w której portret staje się lustrem duszy bohatera.', 46, 15),
  (5, '2025-06-06 10:00:00', 'Bogaty w detale portal do świata japońskich gejs, w którym główna bohaterka odkrywa siłę w tradycji, ale i cenę własnej wolności.', 47, 16),
  (5, '2025-06-06 11:00:00', 'Subtelna i poetycka opowieść o zakazanej fascynacji młodej dziewczyny artystą, która odsłania kulisy renesansowego malarstwa.', 48, 17),
  (5, '2025-06-06 12:00:00', 'Niezwykła i metafizyczna podróż chłopca i tygrysa po pustym oceanie, która stawia pytania o wiarę i granice ludzkiej wytrzymałości.', 49, 18),
  (5, '2025-06-06 13:00:00', 'Przerażający i doskonale skonstruowany thriller psychologiczny, w którym konfrontacja z Hannibalem Lecterem zapada w pamięć na długo.', 50, 19),
  (5, '2025-06-06 14:00:00', 'Pełna humoru i naukowych szczegółów opowieść o przetrwaniu na Marsie, która inspiruje wiarą w ludzką pomysłowość.', 51, 20),
  (5, '2025-06-06 15:00:00', 'Dynamiczna mieszanka popkultury lat 80. z dystopijną wizją przyszłości, w której wirtualna rzeczywistość staje się ucieczką od szarej codzienności.', 52, 4),
  (5, '2025-06-06 16:00:00', 'Monumentalna opowieść o apokalipsie i walce dobra ze złem, w której King buduje społeczność przetrwania na pograniczu świata i zaświatów.', 53, 5),
  (5, '2025-06-06 17:00:00', 'Przerażająca historia o mrocznych tajemnicach dzieciństwa i potworze, który zamieszkuje stary kanał, pozostawiająca czytelnika w stanie nerwowego napięcia.', 54, 6),
  (5, '2025-06-06 18:00:00', 'Poruszająca i ciepła opowieść o przyjaźni i odważnym sprzeciwie czarnoskórych służących przeciwko rasizmowi w latach 60. Missisipi.', 55, 7),
  (5, '2025-06-06 19:00:00', 'Pekinujący policyjny thriller, w którym Lisbeth Salander walczy o prawdę, a zbrodnia splata się z brutalną rzeczywistością.', 56, 8),
  (5, '2025-06-06 20:00:00', 'Emocjonujące zwieńczenie trylogii, w którym Lisbeth Salander staje do ostatecznej walki z demonicznym systemem.', 57, 9),
  (5, '2025-06-06 21:00:00', 'Kontrowersyjny romans erotyczny, w którym relacja Anastazji Steele i Christiana Greya prowokuje do dyskusji o granicach intymności.', 58, 10),
  (5, '2025-06-06 22:00:00', 'Wzruszająca opowieść o miłości i poświęceniu, która stawia trudne pytania o jakość życia i prawo do decydowania o sobie.', 59, 11),
  (5, '2025-06-06 23:00:00', 'Pełna pasji i tajemnic podróż Claire do XVII-wiecznej Szkocji, w której miłość pokonuje bariery czasu.', 60, 12),
  (5, '2025-06-07 00:00:00', 'Rozbudowująca świat Panem kontynuacja, w której Katniss i Peeta odkrywają prawdę o reżimie i bezwzględnej władzy.', 61, 13),
  (5, '2025-06-07 01:00:00', 'Emocjonujące zakończenie trylogii, w którym rebelia przeciwko Kapitolowi sięga zenitu, a Katniss staje przed bolesnymi wyborami.', 62, 14),
  (5, '2025-06-07 02:00:00', 'Pełna magii i symboliki baśń, w której cztery dzieci odkrywają tajemniczy świat Narnii, by wspomóc Aslana w walce ze złem.', 63, 15),
  (5, '2025-06-07 03:00:00', 'Urocza i wzruszająca opowieść o przyjaźni między świnią a pająkiem, która pokazuje, że dobroć potrafi zmienić losy.', 64, 16),
  (5, '2025-06-07 04:00:00', 'Prosta, lecz głęboka historia o dziecięcych fantazjach i tęsknocie za domem, ilustrowana pięknymi, plastycznymi rysunkami.', 65, 17),
  (5, '2025-06-07 05:00:00', 'Pełna energii i humoru książeczka, która zachęca do odkrywania świata wyobraźni przy pomocy zwariowanego kota w meloniku.', 66, 18),
  (5, '2025-06-07 06:00:00', 'Zabawna i pouczająca opowieść ucząca otwartości na nowe doświadczenia, napisana rytmicznym wierszem Dr. Seussa.', 67, 19),
  (5, '2025-06-07 07:00:00', 'Kojąca i poetycka opowieść na dobranoc, w której proste rymy i ciepłe ilustracje wprowadzają dziecko w świat snu.', 68, 20),
  (5, '2025-06-07 08:00:00', 'Kolorowa i edukacyjna bajka o przemianie gąsienicy w motyla, która uczy liczenia i dni tygodnia.', 69, 4),
  (5, '2025-06-07 09:00:00', 'Inspirująca i optymistyczna lektura dla każdego, kto staje przed nowymi wyzwaniami, przypominająca, że warto wierzyć w siebie.', 70, 5),
  (5, '2025-06-07 10:00:00', 'Prosta, rytmiczna książka obrazkowa, która wprowadza najmłodszych w świat kolorów i zwierząt.', 71, 6),
  (5, '2025-06-07 11:00:00', 'Klasyczna, pełna ciepła i humoru opowieść o misiach i przyjaciołach w Stumilowym Lesie, która uczy wartości przyjaźni.', 72, 7),
  (5, '2025-06-07 12:00:00', 'Mroczniejsza i bardziej zagadkowa kontynuacja przygód młodego czarodzieja, która wprowadza nowe tajemnice Hogwartu.', 73, 8),
  (5, '2025-06-07 13:00:00', 'Pełna napięcia i emocji część, w której pojawiają się niebezpieczne tajemnice przeszłości Harry’ego i wierny towarzysz Sirius Black.', 74, 9),
  (5, '2025-06-07 14:00:00', 'Rozbudowana, pełna adrenaliny opowieść o Turnieju Trójmagicznego, w której młodzi bohaterowie mierzą się z ekstremalnymi wyzwaniami.', 75, 10),
  (5, '2025-06-07 15:00:00', 'Mroczniejsza odsłona serii, pokazująca, jak trudna walka z siłami zła wpływa na grupę przyjaciół i cały świat czarodziejów.', 76, 11),
  (5, '2025-06-07 16:00:00', 'Przygotowująca do ostatecznego starcia część, w której odkrywamy bolesne sekrety przeszłości Voldemorta i rosnące zagrożenie.', 77, 12),
  (5, '2025-06-07 17:00:00', 'Emocjonujące zakończenie sagi, w którym młodzi bohaterowie stają do decydującej bitwy o przyszłość czarodziejskiego świata.', 78, 13),
  (5, '2025-06-07 18:00:00', 'Zawiła sieć intryg politycznych i fantastycznych zwrotów akcji, która pokazuje, że w walce o Żelazny Tron liczy się każdy najmniejszy sojusz.', 79, 14),
  (5, '2025-06-07 19:00:00', 'Rozbudowujące uniwersum Westeros rozsypanie pionków na szachownicy polityki, gdzie nowe sojusze mogą okazać się złudne.', 80, 15),
  (5, '2025-06-07 20:00:00', 'Pełna dramatycznych zwrotów akcji i tragicznych wydarzeń część, w której zdrady i intrygi osiągają punkt kulminacyjny.', 81, 16),
  (5, '2025-06-07 21:00:00', 'Skupiająca się na nowych perspektywach i bohaterach odsłona, która pokazuje cenę zwycięstwa i chaos w pogrążonym w wojnie królestwie.', 82, 17),
  (5, '2025-06-07 22:00:00', 'Dynamiczna kontynuacja, w której splatają się losy dawnych i nowych bohaterów, a tyrania i pragnienie władzy nie znają granic.', 83, 18),
  (5, '2025-06-07 23:00:00', 'Poruszająca opowieść o młodej nigeryjskiej dziewczynie, która walczy o prawo do edukacji i własnego głosu.', 84, 19),
  (5, '2025-06-08 00:00:00', 'Delikatna i autentyczna historia skomplikowanej relacji dwojga młodych ludzi, którzy uczą się siebie nawzajem i dorastają emocjonalnie.', 85, 20),
  (5, '2025-06-08 01:00:00', 'Pełna mitologicznych odniesień i poetyckiego języka reinterpretacja losów legendarnej czarodziejki, w której siła kobiecej niezależności staje się najważniejsza.', 86, 4),
  (5, '2025-06-08 02:00:00', 'Intrygujący thriller psychologiczny, w którym niespodziewany twist zmienia perspektywę całej historii i sprawia, że nic nie jest tym, na co wygląda.', 87, 5),
  (5, '2025-06-08 03:00:00', 'Przepiękna, melancholijna opowieść o odludnej dziewczynie, która znajduje w przyrodzie siłę, ale musi zmierzyć się z podejrzeniami społeczności.', 88, 6),
  (5, '2025-06-08 04:00:00', 'Złożona i poruszająca narracja o rodzinie, rasie i tożsamości, w której drobne kłamstwa prowadzą do nieoczekiwanych tragedii.', 89, 7),
  (5, '2025-06-08 05:00:00', 'Mocne i satysfakcjonujące dopełnienie „Opowieści podręcznej”, które z różnych perspektyw ukazuje upadek reżimu Gilead.', 90, 8),
  (5, '2025-06-08 06:00:00', 'Pełna naukowego optymizmu historia o ratowaniu ludzkości w kosmosie, w której samotny astronauta staje przed największym wyzwaniem swojego życia.', 91, 9),
  (5, '2025-06-08 07:00:00', 'Filozoficzna i ciepła opowieść o alternatywnych życiorysach, która pokazuje, że każda decyzja kształtuje naszą tożsamość.', 92, 10),
  (5, '2025-06-08 08:00:00', 'Wnikliwa analiza tożsamości i rasy, w której losy bliźniaczek ujawniają głębokie napięcia społeczne i rodzinne tajemnice.', 93, 11),
  (5, '2025-06-08 09:00:00', 'Hipnotyzująca, melancholijna opowieść o kobietach, którym czas zabiera pamięć, ale pragną pozostać sobą na zawsze.', 94, 12),
  (5, '2025-06-08 10:00:00', 'Dramat psychologiczny ukazujący wpływ toksycznej matczynej miłości na życie bohaterki, która próbuje wyrwać się z kręgu przemocy emocjonalnej.', 95, 13),
  (5, '2025-06-08 11:00:00', 'Wzruszająca i przemyślana refleksja nad naturą świadomości i empatii, która z perspektywy sztucznej istoty bada granice ludzkiej miłości.', 96, 14);
